class CreateEventPage extends ConsumerStatefulWidget {
  const CreateEventPage({super.key, this.isEditScreen = false, this.event});

  final bool isEditScreen;
  final Event? event;

  @override
  ConsumerState<CreateEventPage> createState() => _CreateEventPageState();
}

class _CreateEventPageState extends ConsumerState<CreateEventPage> {
  final _formKey = GlobalKey<FormState>();
  var _enteredTitle;
  var _enteredEventDate;
  var _enteredTimeUnit;
  var _enteredIcon = 'â³';
  int _enteredColor = Colors.black.value;
  var _userEventDate;

// Color for the picker shown in Card on the screen.
  late Color screenPickerColor;
  // Color for the picker in a dialog using onChanged.
  late Color dialogPickerColor;
  // Color for picker using the color select dialog.
  late Color dialogSelectColor;

  @override
  void initState() {
    super.initState();
    screenPickerColor = Colors.blue; // Material blue.or.
  }

  @override
  void dispose() {
    // _formKey.currentState!.dispose();
    super.dispose();
  }

  void _saveEvent() {
    _formKey.currentState!.save();
    if (_formKey.currentState!.validate()) {
      Navigator.pop(context);
      if (widget.isEditScreen) {
        ref.read(userEventsProvider.notifier).editEvent(
              Event(
                id: widget.event!.id,
                title: _enteredTitle,
                eventDate: _enteredEventDate.toString(),
                timeUnit: _enteredTimeUnit.toString(),
                color: _enteredColor,
                icon: _enteredIcon,
              ),
            );
        return;
      }

      try {
        ref.read(userEventsProvider.notifier).addEvent(
              Event(
                title: _enteredTitle,
                eventDate: _enteredEventDate.toString(),
                timeUnit: _enteredTimeUnit.toString(),
                color: _enteredColor,
                icon: _enteredIcon,
              ),
            );

        print(
            'title: $_enteredTitle, eventDate: $_enteredEventDate, timeUnit: $_enteredTimeUnit, color: $_enteredColor ${_enteredColor.runtimeType}, icon: $_enteredIcon ${_enteredIcon.runtimeType}');
      } catch (e) {
        print('UNSUCCESSFUL!!!!! $e');
      }
    }
  }

  Widget colorPickerWidget() {
    return SizedBox(
      width: double.infinity,
      child: Card(
        color: Theme.of(context).colorScheme.background,
        child: ColorPicker(
          // Use the screenPickerColor as start color.
          color: screenPickerColor,
          // Update the screenPickerColor using the callback.
          onColorChanged: (Color color) =>
              setState(() => screenPickerColor = color),
          width: 44,
          height: 44,
          borderRadius: 8,
          pickersEnabled: const <ColorPickerType, bool>{
            ColorPickerType.accent: false,
          },
          enableShadesSelection: false,
          padding: const EdgeInsets.symmetric(vertical: 4),
        ),
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    if (widget.isEditScreen) {
      _enteredTitle = widget.event!.title;
      _userEventDate = DateTime.parse(widget.event!.eventDate);
    }

    return Scaffold(
      appBar: AppBar(
        title: widget.isEditScreen
            ? const Text('Edit Event')
            : const Text('Create Event'),
        actions: [
          IconButton(onPressed: _saveEvent, icon: const Icon(Icons.save))
        ],
      ),
      body: SingleChildScrollView(
        child: Padding(
          padding: const EdgeInsets.all(16.0),
          child: Form(
            key: _formKey,
            child: Column(
              children: [
                //
                // TITLE
                //
                TextFormField(
                  initialValue: _enteredTitle,
                  maxLength: 30,
                  decoration: const InputDecoration(
                      border: OutlineInputBorder(),
                      label: Text('Title'),
                      counterText: ''),
                  validator: (value) {
                    if (value == null ||
                        value.isEmpty ||
                        value.trim().length <= 1 ||
                        value.trim().length > 30) {
                      return 'Must be between 1 and 30 characters';
                    }
                    return null;
                  },
                  onSaved: (newValue) {
                    _enteredTitle = newValue!;
                  },
                ),
                //
                // EVENT DATE
                //
                Padding(
                  padding: const EdgeInsets.only(top: 16.0),
                  child: DateTimeFormField(
                    decoration: const InputDecoration(
                      border: OutlineInputBorder(),
                      suffixIcon: Icon(Icons.event_note),
                      labelText: 'Event Date and Time',
                    ),
                    mode: DateTimeFieldPickerMode.dateAndTime,
                    autovalidateMode: AutovalidateMode.onUserInteraction,
                    initialValue: _userEventDate,
                    validator: (e) =>
                        (e == null) ? 'Please enter a valid date' : null,
                    onDateSelected: (DateTime value) {
                      _enteredEventDate = value;
                    },
                  ),
                ),
                //
                // COUNTER UNIT
                //
                Padding(
                  padding: const EdgeInsets.only(top: 16),
                  child: DropdownButtonFormField(
                    decoration: const InputDecoration(
                      border: OutlineInputBorder(),
                      labelText: 'Counter Unit',
                    ),
                    items: CounterUnits.values.map((counterUnit) {
                      return DropdownMenuItem(
                          value: counterUnit, child: Text(counterUnit.name));
                    }).toList(),
                    validator: (e) =>
                        (e == null) ? 'Please enter a valid date' : null,
                    onChanged: (value) {
                      setState(() {
                        _enteredTimeUnit = value!;
                      });
                    },
                  ),
                ),
                //
                // ICON SELECTOR
                //
                Padding(
                  padding: const EdgeInsets.only(top: 16.0),
                  child: Row(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    children: [
                      Text(
                        _enteredIcon,
                        style: const TextStyle(fontSize: 48),
                      ),
                      // ),
                      const SizedBox(width: 16),
                      Expanded(
                        child: TextFormField(
                          initialValue: _enteredIcon,
                          decoration: const InputDecoration(
                              border: OutlineInputBorder(),
                              label: Text('Icon')),
                          validator: (value) {
                            if (!emojiRegex.hasMatch(value.toString())) {
                              return 'Enter Valid Emoji';
                            }
                            return null;
                          },
                          onChanged: (value) {
                            if (emojiRegex.hasMatch(value.toString())) {
                              setState(() {
                                _enteredIcon = emojiRegex.stringMatch(value)!;
                              });
                            }
                          },
                          onSaved: (newValue) {
                            _enteredIcon = emojiRegex.stringMatch(newValue!)!;
                          },
                        ),
                      ),
                    ],
                  ),
                ),
                //
                // EVENT COLOR
                //
                Container(
                  margin: const EdgeInsets.only(top: 16.0),
                  decoration: BoxDecoration(
                    border: Border.all(color: Colors.grey.shade600),
                    borderRadius: const BorderRadius.all(Radius.circular(8.0)),
                  ),
                  child: Column(
                    children: [
                      ListTile(
                        title: const Text('Select Color'),
                        subtitle:
                            Text(ColorTools.nameThatColor(screenPickerColor)),
                        trailing: ColorIndicator(
                          width: 122,
                          height: 44,
                          borderRadius: 8,
                          color: screenPickerColor,
                        ),
                      ),
                      colorPickerWidget(),
                    ],
                  ),
                ),
                Padding(
                  padding: const EdgeInsets.only(top: 16),
                  child: Column(
                    children: [
                      ElevatedButton(
                          onPressed: _saveEvent, child: const Text('Save')),
                      TextButton(
                          onPressed: () => Navigator.pop(context),
                          child: const Text('Cancel')),
                    ],
                  ),
                ),
              ],
            ),
          ),
        ),
      ),
    );
  }
}
